         << ' 分数: ' << s.test
         << endl;
}

// 地址传递(要用到指针)
void pncout(struct sd *s)
{
    s->name = '你'; // 全局修改
    cout << '子函数中的地址传递输出 ' << endl
         << ' 姓名: ' << s->name
         << endl
         << ' 年龄: ' << s->age << endl
         << ' 分数: ' << s->test
         << endl;
}

int main(void)
{
