> main.cpp

```cpp
#include <iostream>
#include'dian.h'
#include'yuan.h'
using namespace std;


void if_yuan(yuan &c, dian &p)
{
    // 计算两点之间距离的平分
    int jieshou =
    (c.huoqu_o().huoqu_x() - p.huoqu_x()) * (c.huoqu_o().huoqu_x() - p.huoqu_x()) +
    (c.huoqu_o().huoqu_y() - p.huoqu_y()) * (c.huoqu_o().huoqu_y() - p.huoqu_y());

    // 计算半径的平方
    int r_pingfang = c.huoqu_r() * c.huoqu_r();

    // 判断关系
    if (jieshou == r_pingfang){
        cout<<'点在圆上'<<endl;

    }else if(jieshou > r_pingfang)
    {
        cout<<'点在圆外'<<endl;

    }else{
        cout<<'点在圆内'<<endl;

    }
    
}

int main(void)
{
    //创建一个圆
    yuan c;
    c.shezhi_r(10);
    dian center;
    center.shezhi_x(10);
    center.shezhi_y(0);
    c.shezhi_o(center);
    //创建一个点
    dian p;
    p.shezhi_x(10);
    p.shezhi_y(10);

    //判断关系
    if_yuan(c,p);


    return 0;
}
```



